var lazy = $('').lazyRule((MY_HOME) => {
    let from = input.split("#")[1]
    var jx = request(MY_HOME + '/player/' + from + '.js').match(/src="(.*?)'/)[1]
    var urls = jx + input
    try {
        if (from == 'one') {
            var list = pdfa(request(urls), '#lines&&a')
            var name = list.map(data => pdfh(data, 'a&&Text'))
            var url = list.map(data => request(pdfh(data, 'a&&id')).match(/url.*?'(.*?)'/)[1])
            return JSON.stringify({
                urls: url,
                names: name
            });
        }
        var url = request(urls).match(/url.*?'(.*?)'/)[1];
        return url + '#isVideo=true#'
    } catch (e) {}
    return 'video://' + urls
}, MY_HOME);

