var lazy = $('').lazyRule((playBtn, MY_HOME) => {
    try {
        let from = input.split("#")[1];
        var jss = pdfh(fetch(playBtn), '.detailPosterIntro&&script,2&&src')
        log(fetch(jss))
        try {
            var js = fetch(jss).match(/this.Path=(.*?);/)[1].split('"')[1]
        } catch (e) {
            var js = eval(fetch(jss).match(/eval(.*)/)[1]).match(/this.Path=(.*?);/)[1].split('"')[1]
