        if (urll.startsWith("eval")) { 
            urll = eval(urll.match(/eval(.*)/)[1])
        }else
        if (urll.includes("switch")) { 
            urll = urll
        }else{
            urll = eval(urll.match(/var[\s\S]*?;/)[0]);
}        
