                window.__count = window.__count + 1;
                if (window.__count > 0 && window.__count != null && window.__count != undefined) {
                    fba.log('æ—¥å¿—æ£€ç´¢ç¬¬' + window.__count + 'æ¬¡');
                    for (var i in urls) {
