var lazy = $('').lazyRule(() => {
    try {
        var html = JSON.parse(request(input).match(/r player_.*?=(.*?)</)[1])
        var url = html.url
        if (html.encrypt == '1') {
            url = unescape(url);
        } else if (html.encrypt == '2') {
            url = unescape(base64Decode(url));
        }
        if (/m3u8|mp4/.test(url)) {
        var url = url.replace('&', '?')
            return url           
        } else {
            var url = url.replace('', 'https://waipian5.com/player/?url=')
            var url = request(url, {
                headers: {
                    'Referer': 'http://waipian5.com/'
                }
            }).match(/url": "(.*?)"/)[1]
            log(url)
            return url
        }
    } catch (e) {
            return $.require('hiker://page/dmLazy.js?rule=dm盒子').dmLazy();
    }
})

